import Main from './Main';
import Header from './Header';
import Footer from './Footer';
import { useEffect, useState } from 'react';
import { Route, Switch, useHistory, Redirect } from 'react-router-dom';
import Api from '../utils/api';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import api from '../utils/api';
import AddPlacePopup from './AddPlacePopup';
import DeletePopup from './DeletePopup';
import Login from './Login';
import Register from './Register';
import ProtectedRoute from './ProtectedRoute';
import { checkToken, authorize, register } from '../utils/auth';

function App() {
	const history = useHistory();
	const [cards, setCards] = useState([]);
	const [currentUser, setCurrentUser] = useState({});
	const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
	const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
	const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
	const [selectedCard, setSelectedCard] = useState(null);
	const [isDeletePopupOpen, setIsDeletePopupOpen] = useState(false);
	const [cardToDelete, setCardToDelete] = useState(null);
	const [loggedIn, setLoggedIn] = useState(false);
	const [error, setError] = useState('');
	const [popOpenFail, setPopOpenFail] = useState(false);
	const [popOpenSuccess, setPopOpenSuccess] = useState(false);
	const [userData, setUserData] = useState({});

	useEffect(() => {
		const tokenCheck = async () => {
			const token = localStorage.getItem('token');
			if (token) {
				try {
					const tokenIsValid = await checkToken(token);
					if (tokenIsValid) {
						setUserData(tokenIsValid);
						setLoggedIn(true);
						fetchCards();
					} else {
						setLoggedIn(false);
					}
				} catch (error) {
					setLoggedIn(false);
				}
			} else {
				setLoggedIn(false);
			}
			setLoading(false);
		};
		tokenCheck();
	}, [loggedIn]);

	if (loading) {
		return <div>Loading...</div>; // Puedes reemplazar esto con un spinner de carga o cualquier otro indicador de carga
	}

	// Verificar el token
	useEffect(() => {
		const tokenCheck = async () => {
			const token = localStorage.getItem('token');
			if (token) {
				try {
					const tokenIsValid = await checkToken(token);
					if (tokenIsValid) {
						setUserData(tokenIsValid);
						setLoggedIn(true);
						fetchCards();
						history.push('/');
					} else {
						setLoggedIn(false);
						history.push('/signin');
					}
				} catch (error) {
					setLoggedIn(false);
					history.push('/signin');
				}
			} else {
				setLoggedIn(false);
				history.push('/signin');
			}
		};
		tokenCheck();
	}, [loggedIn, history]);

	// Obtener las tarjetas
	const fetchCards = async () => {
		try {
			const cards = await Api.getInitialCards('cards');
			setCards(cards);
		} catch (err) {
			console.log(err);
		}
	};

	// get user info
	useEffect(() => {
		const fetchUserInfo = async () => {
			try {
				const userData = await Api.getUserInfo('users/me');
				setCurrentUser(userData);
			} catch (error) {
				console.log(error);
			}
		};
		fetchUserInfo();
	}, []);

	// Registrar un nuevo usuario
	async function handleRegister(password, email) {
		register(password, email)
			.then((res) => {
				if (res) {
					setPopOpenSuccess(true);
					setError('');
				}
			})
			.catch((err) => {
				setError('Error al registrarse');
			});
		setError('');
	}

	async function handleCardLike(card) {
		// Verifica una vez mÃ¡s si a esta tarjeta ya le han dado like
		const isLiked = card.likes.some((i) => i._id === currentUser._id);

		try {
			const newCard = await Api.changeLikeCardStatus(card._id, !isLiked);
			setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
		} catch (error) {
			console.log(error);
		}
	}

	const handleCardDelete = async (card) => {
		try {
			await api.deleteCard('cards', card._id);
			setCards((cards) => cards.filter((c) => c._id !== card._id));
		} catch (err) {
			console.log(err);
		}
		closeAllPopups();
	};

	const handleDeleteClick = (card) => {
		setCardToDelete(card);
		setIsDeletePopupOpen(true);
	};

	const handleCardClick = (card) => {
		document.body.style.overflow = 'hidden';
		setSelectedCard(card);
	};

	const handleEditAvatarClick = () => {
		setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
	};

	const handleProfileClick = () => {
		setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
	};

	const handleAddPlaceClick = () => {
		setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
	};

	const closeAllPopups = () => {
		document.body.style.overflow = 'auto';
		setIsEditProfilePopupOpen(false);
		setIsAddPlacePopupOpen(false);
		setIsEditAvatarPopupOpen(false);
		setSelectedCard(null);
		setIsDeletePopupOpen(false);
	};

	const handleUpdateUser = async (userData) => {
		try {
			const updatedUser = await api.patchUserInfo(userData);
			setCurrentUser(updatedUser);
		} catch (error) {
			console.log(error);
		}
		closeAllPopups();
	};

	const handleUpdateAvatar = async (avatar) => {
		try {
			const updateAvatar = await api.setUserAvatar(avatar);
			setCurrentUser(updateAvatar);
		} catch (error) {
			console.log(error);
		}
		closeAllPopups();
	};

	const handleAddPlaceSubmit = async (card) => {
		try {
			const newCard = await api.addCard(card);
			setCards([newCard, ...cards]);
		} catch (error) {
			console.log(error);
		}
		closeAllPopups();
	};

	return (
		<div className="page__content">
			<CurrentUserContext.Provider
				value={{ currentUser, cardToDelete, loggedIn, userData }}
			>
				<Header />
				<Switch>
					<Route path="/signup">
						<Register onRegister={handleRegister} />
					</Route>
					<Route path="/signin">
						<Login setLoggedIn={setLoggedIn} />
					</Route>
					<Route path="/">
						{loggedIn ? (
							<Main
								cards={cards}
								onCardLike={handleCardLike}
								onCardClick={handleCardClick}
								onDeleteClick={handleDeleteClick}
								isDeletePopupOpen={isDeletePopupOpen}
								onEditPerfil={handleProfileClick}
								onEditAvatar={handleEditAvatarClick}
								onAddPlace={handleAddPlaceClick}
								selectedCard={selectedCard}
								closePopupImage={closeAllPopups}
							/>
						) : (
							<Redirect to="/signin" />
						)}
					</Route>
				</Switch>
				<Footer />
				<EditProfilePopup
					isOpen={isEditProfilePopupOpen}
					onClose={closeAllPopups}
					onUpdateUser={handleUpdateUser}
				/>
				<EditAvatarPopup
					isOpen={isEditAvatarPopupOpen}
					onClose={closeAllPopups}
					onUpdateAvatar={handleUpdateAvatar}
				/>
				<AddPlacePopup
					isOpen={isAddPlacePopupOpen}
					onClose={closeAllPopups}
					onAddPlaceSubmit={handleAddPlaceSubmit}
				/>
				<DeletePopup
					isOpen={isDeletePopupOpen}
					onClose={closeAllPopups}
					ondeleteCard={handleCardDelete}
				/>
			</CurrentUserContext.Provider>
		</div>
	);
}

export default App;
