import { Link, useHistory } from 'react-router-dom';
import InfoTooltip from './InfoTooltip';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { useEffect, useState, useContext } from 'react';
import { authorize } from '../utils/auth';

function Login({ setLoggedIn, setMessage, message, setPopOpenSuccess, onPopOpenSuccess }) {
	const { setIsMobileOpen, isMobileOpen } = useContext(CurrentUserContext);

	const history = useHistory();
	const [formData, setFormData] = useState({
		email: '',
		password: '',
	});
	const [popOpenFail, setPopOpenFail] = useState(false);
	const [closeTooltip, setCloseTooltip] = useState(false);

useEffect(() => {
		if (message) {
			setPopOpenSuccess(true);
			setCloseTooltip(true);
		}
	}, [message, setPopOpenSuccess]);

	function handleSubmit(e) {
		e.preventDefault();
		const { email, password } = formData;
		authorize(password, email)
			.then((res) => {
				if (res.token) {
					setMessage('Inicio de sesión exitoso');
					localStorage.setItem('token', res.token);
					setPopOpenSuccess(true);
					setIsMobileOpen(false);
				}
				if(res.message){
					setMessage('Correo o contraseña incorrectos');
					setPopOpenFail(true);
				}
			})
			.catch((err) => {
				setPopOpenFail(true);
				setMessage('Error al iniciar sesión');
			});
	}
	// useEffect(() => {
	// 	if (onPopOpenSuccess) {
	// 		setPopOpenSuccess(false);
	// 		setFormData({
	// 			email: '',
	// 			password: '',
	// 		});
	// 		setLoggedIn(true);
	// 		history.push('/');
	// 	}
	// }, [onPopOpenSuccess, history, setLoggedIn, setPopOpenSuccess]);

	const handleChange = (e) => {
		setFormData({
			...formData,
			[e.target.name]: e.target.value,
		});
	};
	const handleClosePopUp = () => {
		setPopOpenFail(false);
		setPopOpenSuccess(false);
	};

	return (
		<>
			<div className="register__content">
				<form className="register__form" onSubmit={handleSubmit}>
					<h2 className="register__title">Inicia sesión</h2>
					<input
						className="register__input"
						name="email"
						type="email"
						placeholder="Correo Electronico"
						value={formData.email}
						onChange={handleChange}
					/>
					<input
						className="register__input"
						name="password"
						type="password"
						placeholder="Contraseña"
						value={formData.password}
						onChange={handleChange}
					/>
					<button className="register__button">Inicia sesión</button>
					<p className="register__subtitle">
						¿Aún no eres miembro? Regístrate <Link to="/signup">aquí</Link>
					</p>
				</form>
				<InfoTooltip
					onSuccess={onPopOpenSuccess}
					onFail={popOpenFail}
					onClose={handleClosePopUp}
					message={message}
					setMessage={setMessage}

				/>
			</div>
		</>
	);
}

export default Login;
