import { Link, useHistory } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Error from './Error';
import InfoTooltip from './InfoTooltip';
import { register } from '../utils/auth';

function Register({ setMessage, message, setPopOpenSuccess, onPopOpenSuccess }) {
	const history = useHistory();
	const [popOpenFail, setPopOpenFail] = useState(false);
	const [error, setError] = useState('');
	const [formData, setFormData] = useState({
		email: '',
		password: '',
		confirmPassword: '',
	});

	useEffect(() => {
		if (onPopOpenSuccess) {
				setPopOpenSuccess(false);
				setFormData({
					email: '',
					password: '',
					confirmPassword: '',
				});
				history.push('/signin');

		}
	}, [onPopOpenSuccess, history, setPopOpenSuccess]);

	function handleSubmit(e) {
		e.preventDefault();
		const { email, password, confirmPassword } = formData;
		if (password === confirmPassword) {
			register(password, email)
				.then((res) => {
					if (res.data.email) {
						// setMessage('Registro exitoso');
						// setPopOpenSuccess(true);
						setError('¡Correcto! Ya estás registrado.');
						setFormData({
							email: '',
							password: '',
							confirmPassword: '',
						});
						history.push('/signin');
					}
					if(res.err){
						setMessage('El correo ya existe');
						setPopOpenFail(true);
					}
				})
				.catch((err) => {
					setMessage('Error al registrar');
					setPopOpenFail(true);
				});
			setError('');
		} else {
			setError('Las contraseñas no coinciden');
		}
	}

	const handleChange = (e) => {
		setFormData({
			...formData,
			[e.target.name]: e.target.value,
		});
	};
	const handleClosePopUp = () => {
		setPopOpenFail(false);
		setPopOpenSuccess(false);
	};

	return (
		<>
			<div className="register__content">
				<form className="register__form" onSubmit={handleSubmit}>
					<h2 className="register__title">Regístrate</h2>

					<input
						className="register__input"
						name="email"
						type="email"
						placeholder="Correo Electronico"
						value={formData.email}
						onChange={handleChange}
					/>
					<input
						className="register__input"
						name="password"
						type="password"
						placeholder="Contraseña"
						value={formData.password}
						onChange={handleChange}
					/>
					<input
						className="register__input"
						name="confirmPassword"
						type="password"
						placeholder="Repite la contraseña"
						value={formData.confirmPassword}
						onChange={handleChange}
					/>
					{error ? <Error error={error} /> : null}
					<button className="register__button">Regístrate</button>
					<p className="register__subtitle">
						¿Ya eres miembro? Inicia sesión {<Link to={'/signin'}>aquí</Link>}
					</p>
				</form>
				<InfoTooltip
					onSuccess={onPopOpenSuccess}
					onFail={popOpenFail}
					onClose={handleClosePopUp}
					message={message}
				/>
			</div>
		</>
	);
}

export default Register;
