import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { useContext } from 'react';

import logo from '../images/logo.svg';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
function Header() {
	const { loggedIn, userData } = useContext(CurrentUserContext);
	const email = userData && userData.data ? userData.data.email : null;
	return (
		<>
			<header className="header">
				<img className="logo" src={logo} alt="Logo around the US" />
				<div className="header__user-info">
				<a className="header__link" href='*'>{!loggedIn? `Regístrate` : email}</a>
				<p>{loggedIn? <Link style to='/'>Cerrar sesión</Link>  : null}</p>

				</div>
			</header>
			<div className="error"></div>
		</>
	);
}

export default Header;
